/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, { Component } from 'react';


class Credits extends Component {
  constructor (props) {
    super(props);
    this.state = {
      creditAdded: [],
      accountBalance : props.accountBalance,
    }
  }

  handleSubmit = (e) => {
    e.preventDefault();
    console.log('yay');
    amount = this.refs.amount.value;
    amount = amount.toFixed(2);
    const { creditAdded, accountBalance } = this.state,
    description = this.refs.description.value,
    amount = this.refs.amount.value,
    date = new Date();
    this.setState({
      creditAdded: [...creditAdded, {
        description,
        amount,
        date,
      }],
      accountBalance: accountBalance - this.refs.amount.value,
    }, () => {
      this.refs.description.value='';
      this.refs.amount.value='';
      this.accountBalance =- this.refs.amount.value;
    });
  }

  render () {
    const { creditAdded, accountBalance } = this.state;
    console.log(this.state.creditAdded);


    return(
        <div>
          <ul>
            {creditAdded.map((credit) =>
              <li>{`${credit.description} ${credit.amount} ${credit.date}`} </li>)}
          </ul>
          <div>{`${accountBalance}`}</div>
          <form onSubmit={this.handleSubmit}>
            <div>
              <label>Description</label>
              <input type="text" ref="description" />
            </div>
            <div>
              <label>Amount</label>
              <input type="text" ref="amount" />
            </div>
            <button>Submit</button>
          </form>
        </div>
    )
  }
}

export default Credits;
