/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, { Component } from 'react';


class Credits extends Component {
  constructor (props) {
    super(props);
    this.state = {
      creditAdded: [...props.creditAdded],
      accountBalance : props.accountBalance,
    }
  }

  handleChange = (e) => {
    var d = new Date();
    var newdate = [
      d.getFullYear(),
      ('0' + (d.getMonth() + 1)).slice(-2),
      ('0' + d.getDate()).slice(-2)
    ].join('-');

    this.setState({
      creditAdded: [...creditAdded,{
        description : e.target.elements.description.value,
        amount : e.target.elements.amount.value,
        date : newdate,
      }],
      accountBalance : accountBalance
    })
  }

  handleSubmit = (e) => {
    e.preventDefault();
    console.log('yay');
    const { creditAdded, accountBalance } = this.state,
    description = this.refs.description.value,
    amount = Number(this.refs.amount.value).toFixed(2),
    date = newdate;
    newBalance = this.accountBalance - amount;
    this.setState({
      creditAdded: [...creditAdded, {
        description,
        amount,
        date,
      }],
      accountBalance: newBalance,
    }, () => {
      this.refs.description.value='';
      this.refs.amount.value='';
    });
    this.props.updateCredits(this.state.creditAdded, this.state.accountBalance);
    console.log(this.accountBalance, this.props.creditList, this.props.accountBalance);
  }

  render () {
    const { creditAdded, accountBalance } = this.state;
    console.log(this.state.creditAdded);

    return(
        <div>
          <h1>Credits</h1>
          <Link to='/'>home</Link>
          <ul>
            {creditAdded.map((credit) =>
              <li style={{listStyle:'none'}}>{`${credit.description} : ${credit.amount} ${credit.date}`} </li>)}
          </ul>
          <div>Account balance : {`${accountBalance}`}</div>
          <form onSubmit={this.handleSubmit}>
            <div>
              <label>Description</label>
              <input type="text" value={this.state.description} onChange={this.handleChange} ref="description" />
            </div>
            <div>
              <label>Amount</label>
              <input type="text" value={this.state.amount} onChange={this.handleChange} ref="amount" />
            </div>
            <button>Submit</button>
          </form>
        </div>
    )
  }
}

export default Credits;
