/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, { Component } from 'react';

const todaysDate = new Date();

class Credits extends Component {
  constructor () {
    super();
    this.state = {
      creditAdded: []
    }
  }

  handleSubmit = (e) => {
    console.log('yay');
    const { creditAdded } = this.state;
    description = this.description.value;
    amount = this.description.value;

    const addedCredit = [...artists, {description : description, amount : amount, date: todaysDate}]
    this.setState({creditAdded: addedCredit})
  }

  render () {
    return(
      <div>
        {todaysDate.toLocaleTimeString()}
        <form onSubmit={this.handleSubmit}>
          <div>
            <label>Description</label>
            <input type="text" name="description" value={description} />
          </div>
          <div>
            <label>Amount</label>
            <input type="text" name="amount" value={amount}/>
          </div>
          <button>Submit</button>
        </form>
      </div>
    )
  }
}

export default Credits;
