/*==================================================
src/components/Credits.js

The Credits component contains information for Credits page view.
Note: You need to work on this file for the Assignment.
==================================================*/
import {Link} from 'react-router-dom';
import React, { Component } from 'react';


class Credits extends Component {
  constructor (props) {
    super(props);
    this.state = {
      description: '', //Initializes variables that will be used in creditAdded
      amount: '',
      date:'',
      creditAdded:this.props.creditList,
      accountBalance:this.props.accountBalance //Initializes by using props
    }
  }


  formatDate = (d) => {
    d = [
      d.getFullYear(),
      ('0' + (d.getMonth() + 1)).slice(-2),
      ('0' + d.getDate()).slice(-2)
    ].join('-');
  }

  //when the input is changed, captures it and updates states
  handleChange = (e, type) => {
    this.setState({...this.state, [type]: e.target.value}); 
    console.log(e.target.value);
  }

  //When submitted, stores data and updates parent component's state
  handleSubmit = (e) => {
    e.preventDefault();
    const { creditAdded, accountBalance, description, amount} = this.state; //initializes states

    //records the date when the form was submitted and change its format to YYYY-MM-DD
    var d = new Date(); 
    d = d.slice(0,10);
    // var newdate = [
    //   d.getFullYear(),
    //   ('0' + (d.getMonth() + 1)).slice(-2),
    //   ('0' + d.getDate()).slice(-2)
    // ].join('-');

    // var newId = id+1;

    // var newId = id++;

    //calculates the balance and format it to show up to 2 decimals
    var newBalance = accountBalance - amount;
    newBalance = newBalance.toFixed(2);
    var id = creditAdded[creditAdded.length-1].id + 1;

    //updates states to its proper format
    this.setState({
      creditAdded : [...creditAdded, {
        id:id,
        description,
        amount,
        date: d
      }],
      accountBalance:newBalance,
    }, () => {
      console.log(this.state); //updates parent component's state
      this.props.updateCredits(this.state.creditAdded, this.state.accountBalance);
    });
  }

  render () {
    const { creditAdded, accountBalance } = this.state;

    return(
        <div>
          <h1>Credits</h1>
          <ul>
            {creditAdded.map((credit) =>
              <li style={{listStyle:'none'}}>{`${credit.description} : ${credit.amount} ${credit.date}`} </li>)}
          </ul>
          <div>Account balance : {`${accountBalance}`}</div>
          <form onSubmit={this.handleSubmit}>
              <input type="text" value={this.state.description} onChange={(e) => {this.handleChange(e, "description")}} ref="description" />
              <input type="text" value={this.state.amount} onChange={(e) => {this.handleChange(e, "amount")}} ref="amount" />
            <button>Add Credit</button>
          </form>
          <Link to='/'>Return to Home</Link>
        </div>
    )
  }
}

export default Credits;
